import React, { useState, useEffect } from "react";
import axios from "axios";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");

  // Fetch existing messages upon component mounting
  useEffect(() => {
    axios
      .get("http://192.168.0.108:8000/rizzagges/")
      .then((response) => {
        const fetchedMessages = response.data.map((msg) => ({
          type: "receiver", // assuming all fetched messages are from the 'receiver'
          text: msg.content, // adjust if your backend structure is different
        }));
        setMessages(fetchedMessages);
      })
      .catch((error) => {
        console.error("Error fetching messages:", error);
      });
  }, []);

  const handleSend = (message) => {
    if (message) {
      const rizzaggeData = {
        user: "username",
        content: message,
        date: new Date().toISOString(),
      };

      axios
        .post("http://192.168.0.108:8000/rizzagge/", rizzaggeData)
        .then((response) => {
          setMessages((prevMessages) => [
            ...prevMessages,
            { type: "sender", text: response.data.content },
          ]);
          setInputMessage("");
        })
        .catch((error) => {
          console.error("Error sending message:", error);
        });
    }
  };

  return (
    <div className="flex h-screen items-center justify-center">
      <div className="flex flex-col w-full max-w-md bg-white p-4 rounded-lg shadow">
        {/* Display Messages */}
        <div className="flex-1 overflow-y-auto">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${
                message.type === "sender" ? "justify-end" : "justify-start"
              } m-2`}
            >
              <span className="px-3 py-1 rounded-xl bg-blue-500 text-white">
                {message.text}
              </span>
            </div>
          ))}
        </div>

        {/* Message Input */}
        <div className="flex mt-2">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type a message..."
            className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
          />
          <button
            onClick={() => handleSend(inputMessage)}
            className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatInterface;
