import React, { useState, useEffect } from "react";
import axios from "axios";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Using Axios to fetch the existing messages when the component mounts
    axios
      .get("http://192.168.0.108:8000/rizzagges/")
      .then((response) => {
        setMessages(response.data);
      })
      .catch((error) => {
        console.error("Error fetching messages:", error);
      });

    console.log(messages);
    const websocket = new WebSocket("ws://192.168.0.108:8000/ws/");

    websocket.onopen = () => {
      console.log("Connected to the WebSocket");
    };

    websocket.onmessage = (event) => {
      setMessages([...messages, { type: "receiver", text: event.data }]);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, [messages]);

  const handleSend = (message) => {
    if (message) {
      const rizzaggeData = {
        user: "username", // example value, adjust accordingly
        content: message,
        date: new Date().toISOString(), // example date, adjust if necessary
      };

      // Sending the message using Axios
      axios
        .post("http://192.168.0.108:8000/rizzagge/", rizzaggeData)
        .then((response) => {
          setMessages([
            ...messages,
            { type: "sender", text: response.data.content },
          ]);
          setInputMessage("");
        })
        .catch((error) => {
          console.error("Error sending message:", error);
        });
    }
  };

  return (
    <div className="flex h-screen items-center justify-center">
      <div className="flex flex-col w-full max-w-md bg-white p-4 rounded-lg shadow">
        {/* Display Messages */}
        <div className="flex-1 overflow-y-auto pb-12">
          {messages.map((message, index) => (
            <div key={index} className="flex justify-end m-2">
              <span className="px-3 py-1 rounded-xl bg-blue-500 text-white">
                {message.text}
              </span>
            </div>
          ))}
        </div>

        {/* Fixed Input */}
        <div
          style={{
            position: "fixed",
            left: "50%",
            transform: "translateX(-50%)",
            bottom: 0,
          }}
          className="flex m-2 bg-white shadow-lg max-w-md"
        >
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type a message..."
            className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
          />
          <button
            onClick={() => handleSend(inputMessage)}
            className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatInterface;
