import React from "react";

function MessageBubble({ text }) {
  return (
    <div className="flex justify-start m-2">
      <span className="px-3 py-1 rounded-xl bg-blue-500 text-gray-700">
        {text}
      </span>
    </div>
  );
}

export default MessageBubble;
