import React, { useState, useEffect } from "react";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    const websocket = new WebSocket("ws://127.0.0.1:8000/ws/");

    websocket.onopen = () => {
      console.log("Connected to the WebSocket");
    };

    websocket.onmessage = (event) => {
      setMessages([...messages, { type: "receiver", text: event.data }]);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, [messages]);

  const handleSend = (message) => {
    if (message && ws) {
      ws.send(message);
      setMessages([...messages, { type: "sender", text: message }]);
      setInputMessage("");
    }
  };

  return (
    <div className="flex h-screen items-center justify-center">
      <div className="flex flex-col w-full max-w-md bg-white p-4 rounded-lg shadow">
        {/* Display Messages */}
        <div className="flex-1 overflow-y-auto pb-12">
          {" "}
          {/* Added padding to prevent overlap */}
          {messages.map((message, index) => (
            <div key={index} className="flex justify-end m-2">
              <span className="px-3 py-1 rounded-xl bg-blue-500 text-white">
                {message.text}
              </span>
            </div>
          ))}
        </div>

        {/* Fixed Input */}
        <div
          style={{
            position: "fixed",
            left: "50%",
            transform: "translateX(-50%)",
            bottom: 0,
          }}
          className="flex m-2 bg-white shadow-lg max-w-md"
        >
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type a message..."
            className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
          />
          <button
            onClick={() => handleSend(inputMessage)}
            className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatInterface;
