import React, { useState } from "react";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");

  const handleSend = (message) => {
    setMessages([...messages, { type: "sender", text: message }]);
    setInputMessage("");
  };

  return (
    <div className="flex flex-col h-full justify-between">
      {/* Display Messages */}
      <div className="flex-1 overflow-y-auto">
        {messages.map((message, index) => (
          <div key={index} className="flex justify-end m-2">
            <span className="px-3 py-1 rounded-xl bg-blue-500 text-white">
              {message.text}
            </span>
          </div>
        ))}
      </div>

      {/* Fixed Input */}
      <div className="flex m-2 bg-white shadow-lg sticky bottom-0">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
        />
        <button
          onClick={() => handleSend(inputMessage)}
          className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
