import React, { useState } from "react";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");

  const handleSend = (message) => {
    // For the sake of this example, we're alternating between 'sender' and 'receiver'
    const messageType = messages.length % 2 === 0 ? "receiver" : "sender";
    setMessages([...messages, { type: messageType, text: message }]);
    setInputMessage("");
  };

  return (
    <div className="flex flex-col h-full">
      {/* Display Messages */}
      <div className="flex-1 overflow-y-auto">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${
              message.type === "sender" ? "justify-end" : "justify-start"
            } m-2`}
          >
            <span
              className={`px-3 py-1 rounded-xl ${
                message.type === "sender"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
            >
              {message.text}
            </span>
          </div>
        ))}
      </div>

      {/* Input */}
      <div className="flex m-2">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
        />
        <button
          onClick={() => handleSend(inputMessage)}
          className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
