import React, { useState, useEffect } from "react";
import axios from "axios";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Fetch initial messages from the backend
    axios
      .get("http://192.168.0.108:8000/rizzagges/")
      .then((response) => {
        const fetchedMessages = response.data.map((msg) => ({
          type: "receiver",
          text: msg.content,
        }));
        setMessages(fetchedMessages);
      })
      .catch((error) =>
        console.error("Error fetching initial messages:", error)
      );

    // WebSocket setup
    const websocket = new WebSocket("ws://192.168.0.108:8000/ws/");
    websocket.onopen = () => {
      console.log("Connected to the WebSocket");
    };
    websocket.onmessage = (event) => {
      console.log("Received from WebSocket:", event.data); // Log the data received

      try {
        const receivedData = JSON.parse(event.data); // Attempt to parse the data

        if (receivedData.content) {
          setMessages((prevMessages) => [
            ...prevMessages,
            { type: "receiver", text: receivedData.content },
          ]);
        } else if (typeof receivedData === "string") {
          setMessages((prevMessages) => [
            ...prevMessages,
            { type: "receiver", text: receivedData },
          ]);
        } else {
          console.error(
            "Unexpected message structure from WebSocket:",
            receivedData
          );
        }
      } catch (error) {
        console.error("Error parsing WebSocket data:", error);
      }
    };
    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  const handleSend = (message) => {
    if (message && ws) {
      ws.send(message);
      setMessages([...messages, { type: "sender", text: message }]);
      setInputMessage("");
    }
  };

  return (
    <div className="flex h-screen items-center justify-center">
      <div className="flex flex-col w-full max-w-md bg-white p-4 rounded-lg shadow">
        {/* Display Messages */}
        <div className="flex-1 overflow-y-auto pb-12">
          {messages.map((message, index) => (
            <div
              key={index}
              className={
                message.type === "sender"
                  ? "flex justify-end m-2"
                  : "flex justify-start m-2"
              }
            >
              <span
                className={
                  message.type === "sender"
                    ? "px-3 py-1 rounded-xl bg-blue-500 text-white"
                    : "px-3 py-1 rounded-xl bg-gray-300"
                }
              >
                {message.text}
              </span>
            </div>
          ))}
        </div>

        {/* Fixed Input */}
        <div
          style={{
            position: "fixed",
            left: "50%",
            transform: "translateX(-50%)",
            bottom: 0,
          }}
          className="flex m-2 bg-white shadow-lg max-w-md"
        >
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type a message..."
            className="flex-1 px-3 py-1 rounded-l-xl border border-gray-300 focus:outline-none focus:border-blue-500"
          />
          <button
            onClick={() => handleSend(inputMessage)}
            className="bg-blue-500 text-white px-4 py-1 rounded-r-xl"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatInterface;
